document.addEventListener("DOMContentLoaded",function(){var g=document.querySelector("ul");var h=g.querySelectorAll("nav>ul>li");for(var d=0;d<h.length;d++){h[d].addEventListener("mouseover",function(){var i=this.querySelector("ul");if(i!==null){i.classList.remove("hidden")}})}for(var d=0;d<h.length;d++){h[d].addEventListener("mouseout",function(){var i=this.querySelector("ul");if(i!==null){i.classList.add("hidden")}})}var f=document.querySelectorAll(".products");for(var d=0;d<f.length;d++){f[d].addEventListener("mouseover",function(){var i=this.querySelector("div");if(i!==null){i.classList.add("hidden")}})}for(var d=0;d<f.length;d++){f[d].addEventListener("mouseout",function(){var i=this.querySelector("div");if(i!==null){i.classList.remove("hidden")}})}var c=document.querySelector(".right");var b=document.querySelector(".left");var a=document.querySelector(".slider");var k=a.querySelectorAll("li");var e=0;k[e].classList.remove("hidden");c.addEventListener("click",function(){k[e].classList.add("hidden");e++;if(e===k.length){e=0}k[e].classList.remove("hidden")});b.addEventListener("click",function(){k[e].classList.add("hidden");e--;if(e<0){e=k.length-1}k[e].classList.remove("hidden")});var j=document.getElementsByClassName("list_arrow");for(var d=0;d<j.length;d++){j[d].addEventListener("click",function(){var i=this.nextElementSibling;i.classList.toggle("hidden");console.log(i)})}});